import { Component, Prop, State } from '@stencil/core';
import ProgressBar from 'progressbar.js';
import { API } from '../../services/WidgetHost';
import { css } from 'emotion';
import FormatJS from '../../services/FormatJs';

const present = <svg width="100" height="100" viewBox="0 0 235 180" fill="none" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink">
            <rect width="250.699" height="179.753" fill="url(#pattern0)"/>
            <defs>
            <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
            <use xlinkHref="#image0" transform="scale(0.00136054 0.00189753)"/>
            </pattern>
            <image id="image0" width="735" height="527" xlinkHref="data:image/png;base64,"/>
            </defs>
            </svg>

@Component({
  tag: 'sqh-progress-indicator',
  styleUrl: 'progress-indicator.scss'
})

export class ProgressIndicator {
  @Prop() ishidden: boolean = false;
  @Prop() textcolor: string;
  @Prop() progresstype: string;
  @Prop() earnedmessage: string;
  @Prop() progressmessage: string;
  @Prop() progresswidth: string;
  @Prop() percentagecolor: string;
  @Prop() percentagesize: string;
  @Prop() progresscolor: string;

  @State() rewardStats: any;
  @State() progressMessage: string;
  @State() earnedMessage: string;
  @State() rewardComplete: boolean;
  @State() loading: boolean = true;
 
  LoadingState() {
    return (
      <div class="container-loading">
        <div class="loading-icon">
          <div class="bar1"></div>
          <div class="bar2"></div>
          <div class="bar3"></div>
          <div class="bar4"></div>
          <div class="bar5"></div>
        </div>
      </div>
    )
  }
  async componentWillLoad() {
    const userProgress = await API.graphql.getUserProgress();
    const programRules = await API.graphql.getProgramRules();
    this.loading = false;

    const purchaseTotal = userProgress.customFields[programRules.id + '_totalValue'] || 0;
    const programGoal = programRules.rules.rewardRules.rewardGoal;

    console.log(userProgress)
    console.log(programRules)

    this.rewardStats = {
      amountEarned: userProgress.rewardBalanceDetails[0] ? userProgress.rewardBalanceDetails[0].prettyAvailableValue : null,
      purchaseTotal,
      programGoal,
      progress: Math.floor(((purchaseTotal % programGoal) / programGoal) * 100) / 100,
      progressToGoal:(programGoal - (purchaseTotal % programGoal))
    }
  }
  async componentDidLoad(){
    if(this.rewardStats.purchaseTotal > 0 && this.rewardStats.purchaseTotal % this.rewardStats.programGoal == 0){
      this.rewardComplete = true;
      this.rewardStats.progress = 1
      this.getProgress();
      return;
    }

    this.getProgress();
    this.updateText();
  }

  componentWillUpdate(){
    this.updateText();
  }

  updateText(){
    const progress = {
      amountNeeded:this.rewardStats.progressToGoal
    }
    const earned = {
      amountEarned: this.rewardStats.amountEarned || 0
    }

    this.progressMessage = FormatJS.format(this.progressmessage, progress);
    this.earnedMessage = FormatJS.format(this.earnedmessage, earned)

  }

  getProgress(){
    var bar = new ProgressBar.Path ('#custom-circle', {
      // This has to be the same size as the maximum width to
      // prevent clipping
      strokeWidth: 8,
      trailWidth: 8,
      easing: 'easeInOut',
      duration: 1400,
      text: {
        autoStyleContainer: false
      },
      from: { color: this.progresscolor, width: 8 },
      to: { color: this.progresscolor, width: 8 },
      // Set default step function for all animate calls
      step: (state, circle) => {
        circle.path.setAttribute('stroke', this.progresscolor);
        circle.path.setAttribute('stroke-width', state.width);
      }
    });

    bar.animate(this.rewardStats.progress); 
  }

  progressBar = () => {
    const circleStyle = css`
      transform: scaleX(-1);
    `
    const textStyle = css`
      position: absolute; 
      left: ${this.rewardComplete ? '53.5%' : '55%'}; 
      top:82%; 
      padding: 0px; 
      margin: 0px; 
      transform: translate(-50%, -50%); 
      color: ${this.percentagecolor || 'rgb(0, 157, 245)'}; 
      font-family: Roboto, Helvetica, sans-serif; 
      font-size: ${this.rewardComplete ? '30px' : '34px'};
      font-weight:bold;
    `
    const presentStyle = css`
      position: absolute; 
      left: 51%; 
      top: 52%; 
      transform: translate(-50%, -50%);
    `;
    const percentStyle = css`
      font-size:65%;
      vertical-align: top;
      display: inline-block;
      margin-top: 3px;
      margin-left: ${this.rewardComplete ? '0px' : '-1px'};
    `;
    const completeStyle = css`
      margin-top:0em;
      font-size:16px;
      background-color:#FFF;
    `;

    return [
      <svg class={circleStyle} width="210px" height="210px" viewBox="0 0 133 133" version="1.1" xmlns="http://www.w3.org/2000/svg">
        <g id="blue-semi-circle" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd" stroke-linecap="round">
          <path d="M94.2309355,112 C106.803489,103.004938 115,88.2630912 115,71.6028679 C115,44.2079604 92.8380951,22 65.5,22 C38.1619049,22 16,44.2079604 16,71.6028679 C16,88.1040219 24.0407405,102.723258 36.4101421,111.740781" id="Grey-Semi" stroke="#E9E9E9" stroke-width="6.5"></path>
          <path id="custom-circle" d="M94.2309355,112 C106.803489,103.004938 115,88.2630912 115,71.6028679 C115,44.2079604 92.8380951,22 65.5,22 C38.1619049,22 16,44.2079604 16,71.6028679 C16,88.1040219 24.0407405,102.723258 36.4101421,111.740781" stroke={this.progresscolor} stroke-width="6.5"></path>
        </g>
      </svg>,
      <span class={presentStyle}>{present}</span>,
      <div class={textStyle}>          
        <br/>
        {this.rewardStats && Math.round(this.rewardStats.progress * 100)}<span class={percentStyle}>%</span><br/><p class={completeStyle}>COMPLETE</p>
      </div>
    ];
  }        
  
  render() {
    const wrapperStyle = css`
      color: ${ this.textcolor };
      text-align: center;
    `

    // progressbar-text has to override strange positioning of image inside ProgressBar
    const progressStyle = css`
      position:relative;
      width: ${ this.progresswidth };
      margin: 30px auto;
      margin-top:0;
      .progressbar-text {
        top: ${ this.rewardComplete ? "37%" : "48%" }!important;  
      }
    `

    const progressMessageStyle = css`
      padding:0 20%;
      margin-bottom:0;
    `

    const congratsStyle = css`
      h1 {
        color:#285F62;
        font-size:36px;
        margin-bottom:2px;
      }
      h3 {
        color:#285F62;
        font-size:20px;
        margin:2px 0;
      }
      p {
        margin-top:5px;
        margin-bottom:1.5em;
      }
    `;

    return !this.ishidden && (
    this.loading ? <this.LoadingState /> :
    <div class={wrapperStyle}>
      { this.earnedMessage }
      <div class={progressStyle}>
        <div id="container">
          { this.progressBar() }
        </div>
      </div> 
      { this.rewardComplete ? 
        <div class={congratsStyle}>
          <h1>Congrats!</h1>
          <h3>You just earned another great reward.</h3>
          <br />
          <p>We love rewarding customers like you, keep it up!</p>
          <p>Earn More Rewards</p>
        </div> 
        :
        [<p class={progressMessageStyle}>{this.progressMessage}</p>,<br />]
      }
    </div>
    );
  }
}
